mapper.select:
		SELECT l.id,l.loginName,l.loginPWD,l.lastLoginDate,l.status,L.accountType,l.accountDesc,u.gmusername as custName,u.instkey as custid
		from account l left join gmuser u on l.custid=u.instkey
		where 1=1

		  and l.loginName like #{loginNameLike}


          and u.gmusername like #{custNameLike}


          and l.status=#{status}


          and l.accountType=#{accountType}

        ORDER BY L.loginName,u.gmusername

        limit #{offset},#{rows}

SELECT count(1)
from account l left join gmuser u on l.custid=u.instkey
where 1=1

  and l.loginName like #{loginNameLike}


  and u.gmusername like #{custNameLike}


  and l.status=#{status}


  and l.accountType=#{accountType}

mapper.update:
	   update account


	        loginPWD = #{loginPWD,jdbcType=VARCHAR},


	        status = #{status,jdbcType=INTEGER},


            lastLoginDate = now(),


            accountDesc = #{accountDesc,jdbcType=VARCHAR}


	    where id = #{id,jdbcType=INTEGER}
